<script src="http://ajax.aspnetcdn.com/ajax/jQuery/jquery-1.8.0.min.js"></script> <script src="https://cdnjs.cloudflare.com/ajax/libs/Chart.js/2.1.4/Chart.bundle.min.js"></script> <script type="text/javascript" charset="utf-8" src="https://cdnjs.cloudflare.com/ajax/libs/Chart.js/2.7.1/Chart.min.js"></script> <script type="text/javascript" src="https://cdn.jsdelivr.net/gh/emn178/chartjs-plugin-labels/src/chartjs-plugin-labels.js"></script>
 <!-- your piecelabel --> <script type="text/javascript"> var num = Math.random(); var data = { labels: ["A", "B", "C", "D", "E", "F", "G", "H", "I", "J"], datasets: [ { data: [ Math.random(), Math.random(), Math.random(), Math.random(), Math.random(), Math.random(), Math.random(), Math.random(), Math.random(), Math.random() ], backgroundColor: [ "#f79546", "#9bba57", "#4f81bb", "#5f497a", "#a94069", "#ff5f34", "#41774e", "#003663", "#49acc5", "#c0504e" ], borderWidth: 0, label: "Dataset 1" }] }; window.onload = function() { var ctx8 = $('#chart8').get(0).getContext("2d"); window.theChart8 = new Chart(ctx8, { type: 'pie', data: data, options: { responsive: true, legend: false, maintainAspectRatio : false, animation: false, pieceLabel: { mode:"label", position:"outside", fontSize: 11, fontStyle: 'bold' } } }); </script> <div style="width:150px"> <canvas id="chart8"></canvas> </div>
